import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [form, setForm] = useState({
    firstname: "",
    lastname: "",
    email: "",
    username: "",
  });
  const [users, setUsers] = useState([]);

  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/users");
      setUsers(res.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", form);
      setForm({ firstname: "", lastname: "", email: "", username: "" });
      fetchUsers();
    } catch (error) {
      console.error("Error submitting form:", error);
    }

    try {
    await axios.post("http://localhost/react-serverless-mysql-app/register.php", form); // adjust path as needed

    // Optionally clear form
    setForm({ firstname: "", lastname: "", email: "", username: "" });

    // Fetch updated users (if you still use DB display)
    fetchUsers();
  } catch (error) {
    console.error("Registration failed:", error);
    alert("Something went wrong. Check the backend or browser console.");
  }
  };

  return (
    <div style={{ minHeight: "100vh", padding: "2rem" }} className="flex flex-col items-center">
      <div className="form-container">
        <h1 className="form-title">User Form</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              className="form-field"
              name="firstname"
              placeholder="First Name"
              value={form.firstname}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <input
              className="form-field"
              name="lastname"
              placeholder="Last Name"
              value={form.lastname}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <input
              className="form-field"
              name="email"
              placeholder="Email"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <input
              className="form-field"
              name="username"
              placeholder="Username"
              value={form.username}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <button type="submit" className="form-button">
              Submit
            </button>
          </div>
        </form>
      </div>

      <div className="user-list">
        <h2 className="user-list-title">Users</h2>
        <ul>
          {users.map((u) => (
            <li key={u.id} className="user-list-item">
              <span className="font-medium">{u.firstname} {u.lastname}</span>
              <span> ({u.username}) - {u.email}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
