import React, { useEffect, useState } from "react";
import axios from "axios";
import emailjs from "@emailjs/browser";
import "./App.css";

function App() {
  const [form, setForm] = useState({
    firstname: "",
    lastname: "",
    email: "",
    username: "",
  });

  const [users, setUsers] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const limit = 5; // number of users per page

  const fetchUsers = async (pageNum = 1) => {
    try {
      const res = await axios.get(`http://localhost:5000/users?page=${pageNum}&limit=${limit}`);
      setUsers(res.data.data);
      setPage(res.data.page);
      setTotalPages(res.data.totalPages);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const sendEmail = () => {
    const templateParams = {
      firstname: form.firstname,
      lastname: form.lastname,
      email: form.email,
      username: form.username,
    };

    console.log("Email params:", templateParams);

    return emailjs.send(
      "service_t2yw3kf",    // your EmailJS service ID
      "template_5wqogy6",   // your EmailJS template ID
      templateParams,
      "8GNPEgxB20oSY8Dd_"   // your EmailJS public key
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", form);
      await sendEmail();

      alert("User registered and confirmation email sent!");
      setForm({ firstname: "", lastname: "", email: "", username: "" });
      fetchUsers(page); // reload current page
    } catch (error) {
      console.error("Error submitting form or sending email:", error);
      alert("Something went wrong. Check console for details.");
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/users/${id}`);
      fetchUsers(page); // refresh current page after deletion
    } catch (error) {
      console.error("Error deleting user:", error);
      alert("Failed to delete user.");
    }
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      fetchUsers(newPage);
    }
  };

  return (
    <div style={{ minHeight: "100vh", padding: "2rem" }} className="flex flex-col items-center">
      <div className="form-container">
        <h1 className="form-title">User Form</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input className="form-field" name="firstname" placeholder="First Name" value={form.firstname} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <input className="form-field" name="lastname" placeholder="Last Name" value={form.lastname} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <input className="form-field" name="email" placeholder="Email" value={form.email} onChange={handleChange} required type="email" />
          </div>
          <div className="form-group">
            <input className="form-field" name="username" placeholder="Username" value={form.username} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <button type="submit" className="form-button">Submit</button>
          </div>
        </form>
      </div>

      <div className="user-list mt-6">
        <h2 className="user-list-title">Users</h2>
        <ul>
          {users.map((u) => (
            <li key={u.id} className="user-list-item">
              <span className="font-medium">{u.firstname} {u.lastname}</span>
              <span> ({u.username}) - {u.email}</span>
              <button className="ml-2 text-red-600 hover:underline" onClick={() => handleDelete(u.id)}>Delete</button>
            </li>
          ))}
        </ul>

        {/* Pagination Controls */}
        <div className="flex items-center justify-center gap-4 mt-4">
          <button
            onClick={() => handlePageChange(page - 1)}
            disabled={page === 1}
            className="px-3 py-1 bg-gray-300 rounded disabled:opacity-50"
          >
            Previous
          </button>

          <span>
            Page {page} of {totalPages}
          </span>

          <button
            onClick={() => handlePageChange(page + 1)}
            disabled={page === totalPages}
            className="px-3 py-1 bg-gray-300 rounded disabled:opacity-50"
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
